     */
    static associate(models) {
      // define association here
    }
  }
  Purchases.init({
    mauser: {
      allowNull: false,
      primaryKey: true,
      type: DataTypes.INTEGER,
      references:{
        model: 'Users',
        key: 'mauser'
      }
    },
    maProduct: {
      allowNull: false,
      primaryKey: true,
      type: DataTypes.STRING,
      references:{
        model: 'Products',
        key: 'maProduct'
      }
    },
    sizeId: {
      allowNull: false,
      primaryKey: true,
