const  PriceSale = (item)=>{
    console.log(">>>show oldPrice: ",item.sale)
    item.price = parseInt(item.price.replace(/\./g, "")) ;
    return  item.price - (item.price*(item.sale)/100)

}
module.exports={PriceSale}
